import React, { useEffect } from 'react';
import {
  Flex,
  Stack,
  Heading,
  Text,
  Button,
  Icon,
  useColorModeValue,
  createIcon,
} from '@chakra-ui/react';
import { invitaciones } from '../../data/invitaciones';

const Formulario = ({ idInvitacion }) => {
  const [nombre, setNombre] = React.useState('');
  const [codigoQR, setCodigoQR] = React.useState(0);
  const [isSubmitting, setIsSubmitting] = React.useState(false);

  useEffect(() => {
    setNombre(invitaciones[idInvitacion]?.nombre);
    setCodigoQR(idInvitacion);
  }, [idInvitacion]);

  const handleSubmit = answer => {
    setIsSubmitting(true);

    const requestOptions = {
      method: 'POST',
      headers: { 'content-type': 'application/x-www-form-urlencoded' },
      body:
        'nombre=' +
        nombre +
        '&invite_code=' +
        codigoQR +
        '&confirmacion=' +
        answer,
    };

    fetch(
      'https://script.google.com/macros/s/AKfycbw9pXQ2YtWiuOA5-p2fyJhTgIC8oBuNs54dbagFFcRlPWjdCuQ-yqJDFUX5F0zGhSdLpA/exec',
      requestOptions
    ).then(response => console.log(JSON.stringify(response)));

    setIsSubmitting(false);
  };

  return (
    <Flex
      minH={'50vh'}
      align={'center'}
      justify={'center'}
      py={12}
      bg={useColorModeValue('orange.50', 'orange.800')}
    >
      <Stack
        boxShadow={'2xl'}
        bg={useColorModeValue('white', 'orange.500')}
        rounded={'xl'}
        p={10}
        spacing={8}
        align={'center'}
      >
        <Icon as={NotificationIcon} w={24} h={24} />
        <Stack align={'center'} spacing={2}>
          <Heading
            fontSize={'3xl'}
            color={useColorModeValue('orange.800', 'orange.200')}
          >
            Confirmación de asistencia
          </Heading>
          <Text fontSize={'lg'} color={'gray.500'}>
            ¡Cupos limitados!
          </Text>
        </Stack>
        <Stack spacing={4} direction={{ base: 'column', md: 'row' }} w={'full'}>
          <Text fontSize={'lg'} color={'gray.500'} lineHeight={2.3}>
            Invitado(s):
          </Text>
          <Text fontSize={'lg'} lineHeight={2.3}>
            {nombre}
          </Text>
        </Stack>
        <Stack spacing={4} direction={{ base: 'column', md: 'row' }} w={'full'}>
          <Text fontSize={'lg'} color={'gray.500'} lineHeight={2.3}>
            Código de Invitación
          </Text>
          <Text fontSize={'lg'} lineHeight={2.3}>
            {codigoQR}
          </Text>
        </Stack>
        <Stack align={'center'} spacing={2}>
          <Button
            isLoading={isSubmitting}
            bg={'green.400'}
            rounded={'full'}
            color={'white'}
            width={'200px'}
            flex={'1 0 auto'}
            onClick={() => handleSubmit(true)}
            _hover={{ bg: 'green.500' }}
            _focus={{ bg: 'green.500' }}
          >
            Confirmar presencia 😉
          </Button>
          <Button
            isLoading={isSubmitting}
            bg={'red.400'}
            rounded={'full'}
            color={'white'}
            width={'200px'}
            flex={'1 0 auto'}
            onClick={() => handleSubmit(false)}
            _hover={{ bg: 'red.500' }}
            _focus={{ bg: 'red.500' }}
          >
            Rechazar invitación 🤷
          </Button>
        </Stack>
      </Stack>
    </Flex>
  );
};

